			externalGlobals: {
				// viewerjs: cdn.jsdelivrFastly("Viewer", "dist/viewer.min.js"),
			},
			// import资源文件的映射
			externalResource: {
				// "viewerjs/dist/viewer.css": cdn.jsdelivrFastly(
				// 	"Viewer",
				// 	"dist/viewer.min.css"
				// ),
			},
